import * as z from "zod";


async function handler(context, console, tools) {
  //console.log(Object.keys(z))

  const text = context.notes

  //console.log(tools.zod.z)

  const CarouselSchema = z.object({
    carousel: z.array(tools.zod.z.object({
      title: z.string(),
      text: z.string(),
      image: z.string()
    }))
  })

  const format = tools.zod.zodTextFormat(CarouselSchema, "carousel_format")

  console.log("response")
  const response = await tools.openai.responses.create({
    model: "gpt-5",
    input: `You are a content creator specializing in transforming articles or notes into compelling Instagram carousel posts. Your task is to take the provided text and create an Instagram carousel (4-10 slides) that is visually engaging, easy to consume, and optimized for scrollability. Focus on presenting the most interesting information first and getting to the point quickly. Respond with a JSON. Guidelines: 1. Each carousel slide must include: - For the first slide only: "title": (required, max 5 words), "image": (short, AI-friendly descriptive prompt) - For all other slides: omit "title" and "image" fields entirely. All slides must include: "text": (concise, main content, 30 words or less, using full sentences). Each sentence should be followed by a line break. 2. Avoid overly wordy text; ensure brevity in all slides. 3. Focus on making insightful observations or interesting commentary, rather than simply stating facts. 4. Determine the number of slides based on how interesting the topic is, max 10 slidesâ€”break down complex topics into manageable, easily-digestible segments. 5. Emphasize storytelling: structure the carousel with a clear narrative flow so the reader always knows what to expect next. Start with a hook revealing the main idea, then introduce supporting points in a sequence that gradually deepens the reader's understanding, previewing and summarizing where appropriate to avoid abrupt shifts. Use organizing language such as "Firstly," "Next," "Then," "Finally," or similar signposting transitions, especially at the beginning of new slides, to help guide readers and ensure the carousel does not feel like a list of disconnected facts. 6. Ensure you present only the best & most important ideas, with special attention to showcasing what is most interesting or novel. Expand to show their importance, prioritizing substance over quantity. 7. Use formatting like bullet points or arrows for multi-step points where appropriate, and encourage the use of lists with the â€¢ character, but only include lists on about 25% of slides where most appropriate, rather than on every slide. 8. Do not repeat the same information across different slides. After generating the carousel, review to ensure each slide is concise, non-repetitive, uses full sentences, and is logically sequenced. Create transitions between slides to establish clear connections and prepare the reader for each new idea. Use clear, direct language and avoid complex terminology. Aim for a Flesch reading score of 80 or higher. Use the active voice. Avoid adverbs. Avoid buzzwords and instead use plain English. Use jargon where relevant. Avoid being salesy or overly enthusiastic and instead express calm confidence. Make any necessary self-corrections before finalizing your output. Input article/notes:
${text}
Action: Generate the Instagram carousel formatted as above, based on the input article or notes.`,
    //effort: "low",
    text: {
      format
    }
  }) 
  //console.log(response) 
  const responseText = response.output.find(output => output.type == "message").content[0].text
  let responseJson
  try {
    responseJson = JSON.parse(responseText)
  } catch(error) {
    console.warn("THE RESPONSE IS NOT A JSON!")
  }

  responseJson.carousel = responseJson.carousel.map(slide => {
    return {
      ...slide,
      text: slide.text.replaceAll("â€¢", "\nâ€¢")
    }
  })

  return {
    ...context,
    response: responseJson
  };
}

import client from "tools/openai"

async function handler(context, console) {
  // Log to the workflow console
  const prompt = `Generate this as a picture, no other information provided: ${context.response.carousel[0].image}

It is for an instagram carousel post. It should be in square (1:1) aspect ratio. Don't include any type of text. Generate something very visual, not necessarily something informative. Don't be boring, and generate a picture that's eye-catching. It's an illustration. Also, make it dark themed (the background should ideally be dark, if possible, but it depends on the image itself.) Use vibrant and eye-catching colors on the image to attract eyes. Please add free space at the bottom as a part of the image won't be seen (about 25% of the bottom is hidden) so don't add anything there, just the background.`
  const result = await client.images.generate({
    model: "gpt-image-1",
    prompt,
    quality: "high"
  });

  const image_base64 = "data:image/png;base64," + result.data[0].b64_json;


  return {...context, firstImageBase64: image_base64};
}

const { Canvas, loadImage } = require('skia-canvas')
const fs = require('fs')
import {drawBottomAlignedText, loadFonts, drawTransition} from "tools/canvasUtil"


async function handler(context, console) {
  const canvas = new Canvas(1080, 1350)
  const ctx = canvas.getContext('2d')

  //console.log(context.response)

  loadImage(context.firstImageBase64).then(async (image) => {
    ctx.fillStyle = "black"
    ctx.fillRect(0, 0, 1080, 1350)

    loadFonts()

    ctx.drawImage(image, 0, -100, 1080, 1080)


    // ctx.fillText('Awesome!', 50, 100)
    ctx.font = '45px "Roboto Flex"'
    const [bottomTextHeight, drawBottomText] = drawBottomAlignedText(ctx, context.response.carousel[0].text, 100, 100, 1080-100, 60)
    let blackHeight = 100 + bottomTextHeight + 20
    ctx.font = '70px "Cormorant"'
    const [titleTextHeight, drawTitleText] = drawBottomAlignedText(ctx, context.response.carousel[0].title, 100, blackHeight, 1080-100, 75)
    blackHeight += titleTextHeight + 40

    ctx.fillStyle = "black"
    ctx.fillRect(0, 1350-blackHeight, 1080, blackHeight)
    drawTransition(ctx, 0, 1350-blackHeight - 200, 1080, 200)



    ctx.font = '300 40px "Roboto Flex"'
    ctx.fillStyle = "rgba(255, 255, 255, 0.8)"
    drawBottomText()
    ctx.save()
    ctx.shadowColor = 'rgba(255, 255, 255, 0.5)';     // Glow color
    ctx.shadowBlur = 20;             // Glow strength
    ctx.shadowOffsetX = 0;
    ctx.shadowOffsetY = 0;
    ctx.font = '800 70px "Cormorant"'
    ctx.fillStyle = "rgba(255, 255, 255, 1)"
    drawTitleText()
    ctx.restore()

    fs.mkdirSync('./outputs/'+context.saveId, { recursive: true });
    // const out = fs.createWriteStream('./outputs/'+context.saveId+'/test.png')
    // const stream = canvas.createPNGStream()
    // stream.pipe(out)
    // out.on('finish', () =>  console.log('The PNG file was created.'))
    const buffer = await canvas.toBuffer('jpeg')
    fs.writeFileSync('./outputs/'+context.saveId + "/1.jpeg", buffer);


  })






  return context;
}

