import fetch from 'node-fetch';
import { extractFromHtml  } from '@extractus/article-extractor'
import { stripHtmlTagsSafe, extractVisibleText, getHTML } from "tools/strip"


async function handler(context, console) {
  if(!context.notes) {
    if(!context.url) {
      console.log("NO NOTES AND NO URL! NEED AT LEAST ONE")
    }

    const html = await getHTML(context.url)
    console.log(html.substring(0, 1000))
    if(!html) return null
    let articleData = await extractFromHtml(html)
    if(articleData?.content) {
      return {
        ...context,
        notes: stripHtmlTagsSafe(articleData.content)
      }
    } else {
      return {
        ...context,
        notes: extractVisibleText(html)
      }
    }

  }




  return context;
}

